import React from "react";
import { useState, useEffect } from "react";
import Sidebar from "./Sidebar";
import TaskPlacement from "./TaskPlacement";
import TaskForm from "../Components/TaskForm";
import Navbar from "./Navbar";
import Task from "./Task";
export default function parentComponent() {
	const [formVisibility, setFormVisibility] = useState(false);
	const [taskInstances, setTaskInstances] = useState([]);
	useEffect(() => {
		console.log("Task Instances: " + taskInstances[taskInstances.length - 1]);
	}, [taskInstances]);
	const toggleVisibility = () => {
		setFormVisibility((prevVisibility) => !prevVisibility);
	};
	const createTaskInstances = () => {
		fetch("http://localhost:8080/Task/getInstance")
			.then((res) => res.json())
			.then((result) => {
				console.log("result " + result);
				setTaskInstances((taskInstances) => [...taskInstances, result]);
			})
			.catch((err) => console.log(err));
	};

	return (
		<div>
			<Navbar></Navbar>
			<Sidebar toggleVisibility={toggleVisibility} />
			<TaskPlacement>
				{formVisibility && (
					<TaskForm
						toggleFormVisibility={toggleVisibility}
						displayTaskInstances={createTaskInstances}
					/>
				)}
				{/* {taskInstances.map((instance) => {
					<Task
						key={instance.id}
						title={instance.title}
						dueDate={instance.dueDate}
					></Task>;
				})} */}
			</TaskPlacement>
		</div>
	);
}
