import React from "react";
import { useState } from "react";

export default function TaskForm(prop) {
	const [title, setTitle] = useState("");
	const [taskType, setTaskType] = useState("");
	const [dueDate, setdueDate] = useState("");
	const [personalTaskChecked, setPersonalTaskChecked] = useState(false);
	const [workTaskChecked, setWorkTaskChecked] = useState(false);

	const handleCheckWork = () => {
		if (document.getElementById("personalCheckBox").checked) {
			document.getElementById("personalCheckBox").checked = false;
		}
		setTaskType = "work";
	};
	const handleCheckPersonal = () => {
		if (document.getElementById("workCheckBox").checked) {
			document.getElementById("workCheckBox").checked = false;
		}
		setTaskType("personal");
	};
	const handleClick = (e) => {
		prop.toggleFormVisibility();
		prop.displayTaskInstances();
		e.preventDefault();
		const Task = { title, taskType, dueDate };
		console.log(Task);
		// fetch("http://localhost:8080/Task/add", {
		// 	method: "POST",
		// 	headers: { "Content-Type": "applications/json" },
		// 	body: JSON.stringify(Task),
		// })
		// 	.then(() => {
		// 		console.log("New Task Added");
		// 	})
		// 	.catch(console.error());
	};

	return (
		<>
			<div className="NewTask">
				<h1>New Task</h1>
				<form>
					<div className="newTaskTitle">
						<label>Title:</label>
						<input type="text" onChange={(e) => setTitle(e.target.value)} />
					</div>
					<div className="newTaskCheckboxes">
						<div className="newTaskPersonal">
							<label>Personal:</label>
							<input
								id="personalCheckBox"
								type="checkbox"
								checked={handleCheckPersonal}
							/>
						</div>
						<div className="newTaskWork">
							<label>Work:</label>
							<input
								id="workCheckBox"
								type="checkbox"
								checked={handleCheckWork}
							/>
						</div>
					</div>
					<div className="newTaskDueDate">
						<label>Due Date: </label>
						<input type="text"></input>
					</div>
					<br />
					<button className="newTaskCancelBtn" onClick={handleClick}>
						Cancel
					</button>
					<button className="newTaskCreateBtn" onClick={handleClick}>
						Create
					</button>
				</form>
			</div>
		</>
	);
}
