import React from "react";
import { useState, useEffect } from "react";
import Sidebar from "./Sidebar";
import TaskPlacement from "./TaskPlacement";
import TaskForm from "../Components/TaskForm";
import Navbar from "./Navbar";
import Task from "./Task";
export default function parentComponent() {
	const [formVisibility, setFormVisibility] = useState(false);
	const [taskInstances, setTaskInstances] = useState([]);
	useEffect(() => {
		window.localStorage.setItem("TASKS", JSON.stringify(taskInstances));
	}, [taskInstances]);
	const toggleVisibility = () => {
		setFormVisibility((prevVisibility) => !prevVisibility);
	};
	const createTaskInstances = () => {
		fetch("http://localhost:8080/Task/getInstance")
			.then((res) => res.json)
			.then(
				(result) => console.log(JSON.stringify(result)),
				setTaskInstances([
					...taskInstances,
					<Task
						key={result.id}
						id={result.id}
						tile={result.title}
						dueDate={result.dueDate}
					></Task>,
				])
			)
			.catch((err) => console.log(err));
	};

	return (
		<div>
			<Navbar></Navbar>
			<Sidebar toggleVisibility={toggleVisibility} />
			<TaskPlacement>
				{formVisibility && (
					<TaskForm
						toggleFormVisibility={toggleVisibility}
						displayTaskInstances={createTaskInstances}
					/>
				)}
				{taskInstances}
			</TaskPlacement>
		</div>
	);
}
