import React from "react";
import TaskForm from "../Components/TaskForm";
import { useState } from "react";

export default function Sidebar() {
	const [seen, setSeen] = useState(false);
	function toggleSeen() {
		setSeen(!seen);
	}
	return (
		<div class="SideBar">
			<aside>
				<button class="button" onClick={toggleSeen}>
					Create Task
				</button>
				{seen ? <TaskForm toggle={toggleSeen} /> : null}
			</aside>
		</div>
	);
}
