import React from "react";
import { useState, useEffect } from "react";
import Sidebar from "./Sidebar";
import TaskPlacement from "./TaskPlacement";
import TaskForm from "../Components/TaskForm";
import TaskInfo from "./TaskInfo";
import Navbar from "./Navbar";
import Task from "./Task";
export default function parentComponent() {
	const [formVisibility, setFormVisibility] = useState(false);
	const [taskInstances, setTaskInstances] = useState([]);
	const [taskinfoVisibility, setTaskInfoVisibility] = useState(false);

	useEffect(() => {
		fetch("http://localhost:8080/Task/getInstances")
			.then((res) => res.json())
			.then((result) => {
				setTaskInstances(result);
			})
			.catch((err) => console.log(err));
	});
	const toggleVisibility = () => {
		setFormVisibility((prevVisibility) => !prevVisibility);
		console.log(taskInstances);
	};
	const toggleTaskInfoVisibility = () => {
		setTaskInfoVisibility((prevVisibility) => !prevVisibility);
	};

	return (
		<div>
			<Navbar></Navbar>
			<Sidebar toggleVisibility={toggleVisibility} />
			<TaskPlacement>
				{formVisibility && <TaskForm toggleFormVisibility={toggleVisibility} />}
				{taskInstances.map((instance) => {
					return (
						<Task
							key={instance.id}
							title={instance.title}
							dueDate={instance.dueDate}
							toggleTaskInfoVisibility={toggleTaskInfoVisibility}
						></Task>
					);
				})}
				{taskinfoVisibility && (
					<TaskInfo toggleInfoFormVisibility={toggleTaskInfoVisibility} />
				)}
			</TaskPlacement>
		</div>
	);
}
